function Navbar({ page, go }) {
  const [open, setOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const links = ["Home","About","Products","Services","Gallery","Testimonials","Contact"];

  useEffect(() => {
    const fn = () => setScrolled(window.scrollY > 50);
    window.addEventListener("scroll", fn);
    return () => window.removeEventListener("scroll", fn);
  }, []);

  return (
    <nav className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${scrolled ? "bg-slate-900/95 backdrop-blur-md shadow-lg shadow-black/20" : "bg-transparent"}`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 flex items-center justify-between h-16">
        <button onClick={() => go("Home")} className="flex items-center gap-3 group">
          <img src={LOGO} alt="Shree Ganesh Logo" className="h-10 w-auto object-contain group-hover:scale-105 transition-transform drop-shadow-md" />
        </button>
        <div className="hidden md:flex items-center gap-6">
          {links.map(l => (
            <button key={l} onClick={() => go(l)} className={`text-sm font-medium transition-colors ${page===l ? "text-amber-400" : "text-slate-300 hover:text-white"}`} style={{fontFamily:"'DM Sans',sans-serif"}}>{l}</button>
          ))}
          <a href={`https://wa.me/${WA_NUM}`} target="_blank" rel="noreferrer"
            className="bg-green-500 hover:bg-green-400 text-white text-xs font-bold px-4 py-2 rounded-full flex items-center gap-1.5 transition-colors shadow-md shadow-green-500/30">
            <WAIcon size={16} /> WhatsApp
          </a>
        </div>
        <button onClick={() => setOpen(!open)} className="md:hidden text-white text-xl p-1">{open ? "" : ""}</button>
      </div>
      {open && (
        <div className="md:hidden bg-slate-900 border-t border-slate-800 px-4 pb-4">
          {links.map(l => (
            <button key={l} onClick={() => { go(l); setOpen(false); }}
              className={`block w-full text-left py-3 border-b border-slate-800 text-sm ${page===l ? "text-amber-400" : "text-slate-300"}`}
              style={{fontFamily:"'DM Sans',sans-serif"}}>{l}</button>
          ))}
          <a href={`https://wa.me/${WA_NUM}`} target="_blank" rel="noreferrer"
            className="mt-3 bg-green-500 text-white text-sm font-bold px-4 py-2.5 rounded-lg flex items-center gap-2 w-full justify-center"><WAIcon size={16} /> Chat on WhatsApp</a>
        </div>
      )}
    </nav>
  );
}

