function ContactPage() {
  const [form, setForm] = useState({ name:"", phone:"", city:"", product:"", msg:"" });
  const [sent, setSent] = useState(false);
  const submit = () => {
    if (!form.name || !form.phone) return;
    const txt = `*New Website Enquiry* \n\n*Name:* ${form.name}\n*Phone:* ${form.phone}\n*City:* ${form.city||""}\n*Product:* ${form.product||"General"}\n*Message:* ${form.msg||""}`;
    window.open(`https://wa.me/${WA_NUM}?text=${encodeURIComponent(txt)}`, "_blank");
    setSent(true);
  };
  return (
    <div className="pt-16 bg-slate-50 min-h-screen">
      <div className="bg-slate-900 pt-16 pb-12 px-4 sm:px-6">
        <div className="max-w-7xl mx-auto">
          <div className="text-amber-400 text-xs font-black uppercase tracking-widest mb-2">Get In Touch</div>
          <h1 className="text-4xl font-black text-white" style={{fontFamily:"'Syne',sans-serif"}}>Contact Us</h1>
        </div>
      </div>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 py-16">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          <div>
            <h2 className="text-2xl font-black text-slate-900 mb-8" style={{fontFamily:"'Syne',sans-serif"}}>Visit Our Shop</h2>
            {[["","Address","Dhanora Road, Near Nagar Palika, Ramnagar, Gadchiroli  442605, Maharashtra"],["","Phone","093258 45815"],["","Hours","Open daily  Closes 8:00 PM"],["","Rating","5.0 on Google Maps"]].map(([icon,label,val]) => (
              <div key={label} className="flex gap-4 mb-6">
                <div className="text-2xl w-9 flex-shrink-0">{icon}</div>
                <div>
                  <div className="text-amber-500 text-xs font-black uppercase tracking-widest mb-1">{label}</div>
                  <div className="text-slate-700 text-sm leading-relaxed" style={{fontFamily:"'DM Sans',sans-serif"}}>{val}</div>
                </div>
              </div>
            ))}
            <a href={`https://wa.me/${WA_NUM}`} target="_blank" rel="noreferrer"
              className="inline-flex items-center gap-3 bg-green-500 hover:bg-green-400 text-white font-black px-6 py-3.5 rounded-xl text-sm transition-colors shadow-lg shadow-green-500/30 mt-2">
              <WAIcon size={18} /> Open WhatsApp Chat
            </a>
          </div>
          <div className="bg-white rounded-2xl p-8 shadow-sm border border-slate-100">
            {sent ? (
              <div className="text-center py-12">
                <div className="text-5xl mb-4"></div>
                <h3 className="text-xl font-black text-slate-900 mb-2" style={{fontFamily:"'Syne',sans-serif"}}>Enquiry Sent!</h3>
                <p className="text-slate-500 text-sm mb-6" style={{fontFamily:"'DM Sans',sans-serif"}}>Your message was sent via WhatsApp. We'll reply shortly.</p>
                <button onClick={() => setSent(false)} className="bg-slate-900 text-white font-bold text-sm px-6 py-2.5 rounded-xl">Send Another</button>
              </div>
            ) : (
              <>
                <h3 className="text-xl font-black text-slate-900 mb-6" style={{fontFamily:"'Syne',sans-serif"}}>Send an Enquiry</h3>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-4">
                  {[["name","Your Name *","text"],["phone","Phone Number *","tel"],["city","Your City","text"],["product","Product Interest","text"]].map(([f,l,t]) => (
                    <div key={f}>
                      <label className="block text-xs font-black text-slate-700 mb-1.5 uppercase tracking-wider">{l}</label>
                      <input type={t} value={form[f]} onChange={e => setForm({...form,[f]:e.target.value})} placeholder={l.replace(" *","")}
                        className="w-full border border-slate-200 rounded-xl px-4 py-3 text-sm text-slate-800 bg-slate-50 focus:outline-none focus:border-amber-400 focus:ring-2 focus:ring-amber-400/20 transition"
                        style={{fontFamily:"'DM Sans',sans-serif"}} />
                    </div>
                  ))}
                </div>
                <div className="mb-5">
                  <label className="block text-xs font-black text-slate-700 mb-1.5 uppercase tracking-wider">Message</label>
                  <textarea value={form.msg} onChange={e => setForm({...form,msg:e.target.value})} rows={4} placeholder="Tell us what you need..."
                    className="w-full border border-slate-200 rounded-xl px-4 py-3 text-sm text-slate-800 bg-slate-50 focus:outline-none focus:border-amber-400 focus:ring-2 focus:ring-amber-400/20 resize-none transition"
                    style={{fontFamily:"'DM Sans',sans-serif"}} />
                </div>
                <button onClick={submit}
                  className="w-full bg-gradient-to-r from-amber-400 to-orange-500 hover:opacity-90 text-slate-900 font-black py-4 rounded-xl text-sm transition-opacity shadow-lg shadow-amber-500/30 flex items-center justify-center gap-2"
                  style={{fontFamily:"'Syne',sans-serif"}}>
                  <WAIcon size={18} /> Send via WhatsApp
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

