export default function App() {
  const [page, setPage] = useState("Home");
  const go = (p) => { setPage(p); window.scrollTo({top:0,behavior:"smooth"}); };

  useEffect(() => {
    const l = document.createElement("link");
    l.href = "https://fonts.googleapis.com/css2?family=Syne:wght@700;800;900&family=DM+Sans:wght@400;500;600&display=swap";
    l.rel = "stylesheet";
    document.head.appendChild(l);
  }, []);

  const pages = { Home:<HomePage go={go}/>, About:<AboutPage/>, Products:<ProductsPage/>, Services:<ServicesPage/>, Gallery:<GalleryPage/>, Testimonials:<TestimonialsPage/>, Contact:<ContactPage/> };

  return (
    <div className="bg-slate-50 min-h-screen">
      <Navbar page={page} go={go}/>
      {pages[page] || <HomePage go={go}/>}
      <Footer go={go}/>
      <FloatingWA/>
    </div>
  );
}
